import React, { lazy } from "react";
import PropTypes from "prop-types";
import Loader from "../../Components/Loader";
import Coin from "../../Components/Coin";
import { useParams } from "react-router-dom";

const DetailBlock=({detail})=>{
    return(
        <>
        <h1>{detail.name} / {detail.symbol}</h1>
        <div>{detail.description}</div>
        <div>Rank:<span>{detail.rank}</span></div>
        <div>Open Sourece:{detail.open_source?<span>Yes</span>:<span>No</span>}</div>
        <div>Proof Type:<span>{detail.proof_type}</span></div>
        <div>Structure:<span>{detail.structure}</span></div>
        </>
    )
}
const CoinsDetailPresenter = ({detail,loading}) =>{
    console.log(loading)
    if(!loading){
        console.log(detail)
    }


    return(
        loading?<Loader/>:<DetailBlock detail={detail}/>

    )

};

export default CoinsDetailPresenter;
